mapper.select:

SELECT ac_no, ac_id ,ac_ccode ,ac_cname ,ac_manjok ,ac_review, ac_rdate
FROM community_ac
WHERE ac_ccode = #{ac_ccode}
ORDER BY ac_rdate DESC LIMIT #{limit}

SELECT c_state
FROM community
WHERE c_code = #{c_code}

SELECT s_state, s_id
FROM community_signup
WHERE s_code = #{s_code}

SELECT c_id
FROM community
WHERE c_code = #{c_code}

SELECT b_code, b_name
FROM book
WHERE b_code = #{b_code}

SELECT c_banner
FROM community
WHERE c_code = #{c_code}

mapper.update:
UPDATE community
SET c_name    = #{c_name}
    ,c_des    = #{c_des}
    ,c_local  = #{c_local}
    ,c_chat   = #{c_chat}
    ,c_banner = #{c_banner}
    ,c_count  = #{c_count}
WHERE c_code  = #{c_code}

mapper.select:

SELECT COUNT(*)
FROM community_ac
WHERE ac_ccode = #{c_code}
